--  DDL for View VIEW_BOARDLIST
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "SCOTT"."VIEW_BOARDLIST" ("글번호", "제목", "작성자", "작성일") AS 
  SELECT ROW_NUMBER() OVER(ORDER BY CREATED)"글번호"
     , TITLE"제목", NAME"작성자", CREATED"작성일"
FROM TBL_BOARD
ORDER BY 4 DESC
;
