--   A 의 B 승의 값을 반환하는 사용자 정의 함수를 작성한다.
--   단, 기존의 오라클 내장 함수를 이용하지 않고, 반복문을 활용하여 작성한다.
-- 함수명 : FN_POW()
/*
사용 예)
SELECT FN_POW(10, 3)
FROM DUAL;
--==>> 1000
*/

/*
FN_POW(10, 3)                 → 1000
        기준 1 * 10 * 10 * 10 → 1000
        기준 0 * 10 * 10 * 10 → 0
*/

CREATE OR REPLACE FUNCTION FN_POW (A NUMBER, B NUMBER)
RETURN NUMBER                                     
IS
    V_RESULT    NUMBER := 1;     -- 반환 결과값 변수 → CHECK ~!!! 1로 초기화~!!!
    V_NUM       NUMBER;          -- 루프변수
BEGIN
    -- 반복문 구성
    FOR V_NUM IN 1.. B LOOP
        V_RESULT := V_RESULT * A;   -- V_RESULT *= A;
    END LOOP;

    -- 최종 결과값 반환
    RETURN V_RESULT;    
END;
